import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Home() {
  const [students, setStudents] = useState([]);
  const [render, setRender] = useState(false);
  const [input, setInput] = useState({
    Name: " ",
    Class: " ",
    Age: " ",
  });
